<span class="line">Mar <span class="token number">16</span> 01:15:25 study.centos.mrcode postfix/cleanup<span class="token punctuation">[</span><span class="token number">20434</span><span class="token punctuation">]</span>: 827B31075: message-id<span class="token operator">=</span><span class="token operator">&lt;</span><span class="token number">20200315171525</span>.827B31075@study.centos.mrcode<span class="token operator">></span></span>
<span class="line">Mar <span class="token number">16</span> 01:15:25 study.centos.mrcode postfix/qmgr<span class="token punctuation">[</span><span class="token number">1618</span><span class="token punctuation">]</span>: 827B31075: <span class="token assign-left variable">from</span><span class="token operator">=</span><span class="token operator">&lt;</span>mrcode@study.centos.mrcode<span class="token operator">></span>, <span class="token assign-left variable">size</span><span class="token operator">=</span><span class="token number">449</span>, <span class="token assign-left variable">nrcpt</span><span class="token operator">=</span><span class="token number">1</span> <span class="token punctuation">(</span>queue active<span class="token punctuation">)</span></span>
<span class="line">Mar <span class="token number">16</span> 01:15:25 study.centos.mrcode postfix/local<span class="token punctuation">[</span><span class="token number">20436</span><span class="token punctuation">]</span>: 827B31075: <span class="token assign-left variable">to</span><span class="token operator">=</span><span class="token operator">&lt;</span>dmtsai@study.centos.mrcode<span class="token operator">></span>, <span class="token assign-left variable">orig_to</span><span class="token operator">=</span><span class="token operator">&lt;</span>dmtsai<span class="token operator">></span>, <span class="token assign-left variable">relay</span><span class="token operator">=</span>local, <span class="token assign-left variable">delay</span><span class="token operator">=</span><span class="token number">0.02</span>, <span class="token assign-left variable">delays</span><span class="token operator">=</span><span class="token number">0.02</span>/0/0/0, <span class="token assign-left variable">dsn</span><span class="token operator">=</span><span class="token number">5.1</span>.1, <span class="token assign-left variable">status</span><span class="token operator">=</span>bounced <span class="token punctuation">(</span>unknown user: <span class="token string">"dmtsai"</span><span class="token punctuation">)</span></span>
