<span class="line">Mar <span class="token number">16</span> 01:15:25 study.centos.mrcode postfix/local<span class="token punctuation">[</span><span class="token number">20436</span><span class="token punctuation">]</span>: 87ED81076: <span class="token assign-left variable">to</span><span class="token operator">=</span><span class="token operator">&lt;</span>mrcode@study.centos.mrcode<span class="token operator">></span>, <span class="token assign-left variable">relay</span><span class="token operator">=</span>local, <span class="token assign-left variable">delay</span><span class="token operator">=</span><span class="token number">0.01</span>, <span class="token assign-left variable">delays</span><span class="token operator">=</span><span class="token number">0</span>/0/0/0, <span class="token assign-left variable">dsn</span><span class="token operator">=</span><span class="token number">2.0</span>.0, <span class="token assign-left variable">status</span><span class="token operator">=</span>sent <span class="token punctuation">(</span>delivered to mailbox<span class="token punctuation">)</span></span>
